        match self.peek()? {
            TokenType::Integer(_) | TokenType::True | TokenType::False => {
                Ok(ast::Expression::Literal(self.literal()?))
            }
            _ => Err(error(self.advance()?, "Expression".to_string()))?,
        }
    }

