#[derive(Debug)]
pub enum IntExpression {
    Literal(i32),
    Negate(Box<IntExpression>),
    BinaryOperation(Box<IntExpression>, IntBinaryOp, Box<IntExpression>),
}

#[derive(Debug)]
pub enum IntBinaryOp {
    Plus,
